// Process crawl data
const data = $input.item.json;
const inputData = typeof data.input_data === 'string' ? JSON.parse(data.input_data) : data.input_data;

return {
  json: {
    job_id: data.job_id || data.id,
    organization_id: data.organization_id,
    tool_key: data.tool_key || 'seo-autopilot',
    domain_url: inputData.domain_url || inputData.url,
    domain_id: inputData.domain_id || inputData.seo_domain_id,
    scope: inputData.scope || 'full',
    callback_url: data.callback_url || `http://127.0.0.1:5000/api/webhooks/n8n/callback`
  }
};

// Parse sitemap XML and extract URLs
const xml = $input.item.json;
const xmlString = typeof xml === 'string' ? xml : JSON.stringify(xml);

// Simple XML parsing for <loc> tags
const urlRegex = /<loc>(.*?)<\/loc>/g;
const urls = [];
let match;

while ((match = urlRegex.exec(xmlString)) !== null) {
  urls.push(match[1]);
}

return {
  json: {
    ...items[0].json,
    urls: urls.slice(0, 100), // Limit to 100 URLs
    url_count: urls.length
  }
};
